INSERT INTO `donation_type` VALUES (10,'Y','icon-heli','Thanks for your donation!','Westpac Rescue - Auckland','donorcare@arht.org.nz','<p>We really appreciate your commitment to going above and beyond for your Auckland and Coromandel Westpac Rescue Helicopters by becoming a Mission Sponsor. It&rsquo;s great having committed donors like you onboard. Thank you!</p>\r\n\r\n<p>We wouldn&rsquo;t be able to save hundreds of lives every year without the kind actions of our Mission Sponsors.</p>\r\n\r\n<p>24 hours a day, seven days a week, 365 days of the year we will be there for the elderly to those just starting out in life, when we&rsquo;re needed most.</p>\r\n\r\n<p>It takes just a few good sorts in life to be there for one other life from our community.</p>\r\n\r\n<p>As a new Mission Sponsor, it&rsquo;s great knowing we can count on you.</p>\r\n\r\n<p><em>Your annual donation receipt will be posted to you.</em></p>',15),(11,'N','icon-hand','Thanks for your donation!','Westpac Rescue - Auckland','talwinder@tomahawk.co.nz','<p>Thank you very much for your support of your Auckland and Coromandel Westpac Rescue Helicopters through your kind donation.</p>\r\n\r\n<p>We wouldn&rsquo;t be able to save hundreds of lives a year without our community taking action too.</p>\r\n\r\n<p>24 hours a day, seven days a week, 365 days of the year we will be there for the elderly to those just starting out in life, when we&rsquo;re needed most.</p>\r\n\r\n<p>It takes a lot of good sorts in life to be there for people from all walks of life.</p>\r\n\r\n<p>It&rsquo;s great knowing we can count on you.</p>\r\n\r\n<p>You can expect an email receipt from us within a couple of days.</p>',16),(12,'N','icon-heli','Thanks for your donation!','Westpac Rescue - Auckland','donorcare@arht.org.nz','<p>We really appreciate your commitment to going above and beyond for your Auckland and Coromandel Westpac Rescue Helicopters by way of a regular gift. It&rsquo;s great having committed donors like you onboard. Thank you!</p>\r\n\r\n<p>We wouldn&rsquo;t be able to save hundreds of lives every year without the kind actions of our regular givers.</p>\r\n\r\n<p>24 hours a day, seven days a week, 365 days of the year we will be there for the elderly to those just starting out in life, when we&rsquo;re needed most.</p>\r\n\r\n<p>It takes a lot of good sorts in life to be there for people from all walks of life.</p>\r\n\r\n<p>It&rsquo;s great knowing we can count on you.</p>\r\n\r\n<p><em>Your annual donation receipt will be posted to you.</em></p>',17),(13,'N','icon-heart','','','','',18),(14,'N','icon-piggibank','','','','',19),(15,'N','icon-hand','','','','',69),(16,'N','icon-heart','','','','',70),(17,'N',NULL,'','','','',102);
